<?php\necho "Teste das funcoes checkPlan() e checkPlanPending()\n";\n\nif (function_exists("checkPlan")) {\n    echo "checkPlan() existe\n";\n    try {\n        \$result = checkPlan();\n        echo "checkPlan() executou sem erro (retornou: " . (is_null(\$result) ? "null" : "objeto") . ")\n";\n    } catch (Exception \$e) {\n        echo "Erro em checkPlan(): " . \$e->getMessage() . "\n";\n    }\n} else {\n    echo "checkPlan() nao existe\n";\n}\n\nif (function_exists("checkPlanPending")) {\n    echo "checkPlanPending() existe\n";\n    try {\n        \$result = checkPlanPending();\n        echo "checkPlanPending() executou sem erro (retornou: " . (is_null(\$result) ? "null" : "objeto") . ")\n";\n    } catch (Exception \$e) {\n        echo "Erro em checkPlanPending(): " . \$e->getMessage() . "\n";\n    }\n} else {\n    echo "checkPlanPending() nao existe\n";\n}\n\necho "\nTeste concluido.\n";